const moment = require('moment');
const jsonfile = require('jsonfile')
export default async (req, res) => {
  let { date = 0,count = 0 } = req.query,monthData;
  if(!date) return res.json({data:[]})
  const { imgArr } = global;
  //  28
  /*if(Number(date)){
      console.log(date)
      date=String(date).slice(0,6)
      monthData=imgArr.filter(i=>String(i.date).includes(date))
      //今天距离月初的天数
      // count=new Date(date.slice(4), date.slice(2), 0).getDate()
  }else {
      const now = moment().format('YYYYMM');
      monthData=imgArr.filter(i=>String(i.date).includes(now))
      if(monthData.length<28){
          const last=moment().subtract(1, 'month').format('YYYYMM')
          const lastMonthData=imgArr.filter(i=>String(i.date).includes(last))
          console.log(imgArr.length,'lastMonthData')
          monthData=monthData.concat(lastMonthData)
      }
  }*/
    //读取后一个月的数据保证始终超过10条
    const currentFileName = date.slice(0,6)
    const laseFileName = moment(date || new Date()).subtract(1,'months').format('YYYYMM');
    const {imgs:currentMonthData} = await jsonfile.readFile(`../../bing_data/${currentFileName}.json`).catch(() => ({imgs:[]}))
    const {imgs:lastMonthData} = await jsonfile.readFile(`../../bing_data/${laseFileName}.json`).catch(() => ({imgs:[]}))
        .catch(() => ({}))
    let startIndex = imgArr.findIndex(({ date: d }) => date === `${d}`);
    (startIndex < 0) && (startIndex = imgArr.length-1);
  // console.log(currentMonthData[0]?.date,lastMonthData[0]?.date)
  console.log(currentMonthData[0],lastMonthData[0])
  res.json({
    data: [...[...currentMonthData,...lastMonthData].slice(Math.max(0, startIndex - (count-1)), startIndex + 1)].reverse()
  })
}
