import axios from 'axios';
import {useState, useEffect, useRef} from 'react';
import '../styles/index.scss';
import moment from 'moment';
import Head from "../components/Head";
import DownDialog from "../components/downDialog";
import {Spin} from "antd";
/*export default class Page extends React.Component {
    componentDidMount () {
        likeFun()
    }
    render (){
        return PageFun()
    }
}*/
export default function() {
    const [downDialogVisible, setDownDialogVisible] = useState(false);
    const [list, updateList] = useState([]);
    const [img, updateImg] = useState({});
    const [nextDate, updateNextDate] = useState(0);
    const [loading, updateLoading] = useState(false);
    const [isMobile, setIsMobile] = useState(false);
    const [showTop, setShowTop] = useState(false);
    const [isEmpty, setEmpty] = useState(false);
    const [likeList, setLikeList] = useState([]);
    const checkMode = () => {
        if (window.innerWidth < 1024 || window.innerWidth < window.innerHeight) {
            setIsMobile(true);
            return true;
        }
        setIsMobile(false)
        return false;
    }
    const likeFun = (data) => {
        const dataList=JSON.parse(window.localStorage.getItem('dataList') || '[]')
        if(!likeList.length){
            setLikeList(dataList)
        }
        if(data && !dataList.includes(data)){
            setLikeList(dataList)
            dataList.push(data)
            window.localStorage.setItem('dataList',JSON.stringify(dataList))
        }
    }
    if(typeof window !== 'undefined' && window){
        likeFun()
    }
    const downloadFun = () => {

    }
    const queryList = (nextDate, isMobile) => {
        updateLoading(true);
        // nextDate=String(nextDate).substr(0,6)
        axios(`/api/list?date=${nextDate}&count=28`)
            .then(({data}) => {
                let count = 0;
                const maxCount = isMobile ? 0 : 2;
                data.data.forEach((item, i) => {
                    let span = 1;
                    //0 1 2
                    (count < maxCount) && maxCount && (span = Math.ceil(Math.random() * 2));
                    // 10-i,第8张换span  8 16 24
                    !(i%12) && maxCount && (span = 2);

                    console.log(maxCount, span, isMobile);
                    span === 2 && (count += 1);
                    item.style = {
                        gridColumnStart: `span ${span}`,
                        gridRowStart: `span ${span}`,
                    }
                    item.dateStr = moment(item.date, 'YYYYMMDD').format('YY.MM.DD');
                    item.span = span;
                })
                const last = data.data.last()
                updateList([...list, ...data.data]);
                !last.prev && setEmpty(true);
                updateNextDate(last.prev);
                updateLoading(false);

            })
    }

    const $page = useRef();

    useEffect((e) => {
        window.onscroll = () => {
            if (loading || isEmpty) {
                return;
            }
            setShowTop(document.documentElement.scrollTop > 700);
            if (document.documentElement.clientHeight + document.documentElement.scrollTop + 50 > $page.current.clientHeight) {
                queryList(nextDate, isMobile);
            }
        }
        setTimeout(() => {
            window.onscroll();
        })
    }, [nextDate, loading]);

    useEffect(() => {
        const isMobile = checkMode();
        Array.prototype.last = function () {
            return this[this.length - 1];
        }

        queryList(nextDate, isMobile)
    }, [])
    return (

        <div className={`index-page ${isMobile && 'mobile-page'}`} ref={$page}>
            <Head></Head>
            <div className="img-list"
                 style={{
                     gridTemplateRows: isMobile ? `repeat(${list.length}, 75vw)` : `repeat(${Math.ceil(list.length / 28) * 4}, 19vw)`
                 }}>
                {
                    list.map((img) => (
                        <div
                            className="img-item"
                            style={img.style}
                            key={img.date}
                        >
                            <div className="cover">
                                <a className="iconfont icon-fangda actionBtn" href={`/${img.date}`}/>
                                <span className="text">{img.cp}</span>
                            </div>
                            <div className={`date-str size-${img.span}`}>{img.dateStr}</div>
                            <div className="img-bg"
                                 style={{backgroundImage: `url('//cn.bing.com${img.urlbase}_${false ? '768x1280' : '1024x768'}.jpg')`}}/>
                             <div className="handleButton">
                                 <span className={(likeList.includes(img.date)?'icon-like-fill':'icon-xihuan')+' iconfont'} onClick={() => {likeFun(img.date)}}></span>
                                 <span  onClick={() => {setDownDialogVisible(true);updateImg(img);}} className="iconfont icon-download"></span>
                             </div>
                        </div>
                    ))
                }
            </div>
            {isEmpty && <div className="empty-text">—— 到底啦 ——</div>}
            <div className="right-btn">
                <div className={`to-top iconfont icon-arrow-left ${!showTop && 'hide'}`}
                     onClick={() => window.scrollTo(0, 0)}/>
                <a className="iconfont icon-touzi" href='/random'/>
            </div>
            <DownDialog isMobile={isMobile} visible={downDialogVisible} onHide={() => {setDownDialogVisible(false);updateImg({});}}
                        imgInfo={img}/>
        </div>
    )
}
